sqlMap.select:
        SELECT t.formID FROM (
			SELECT row_number() OVER (ORDER BY f.CREATETIME desc) rowN, f.formID FROM ${FORM} f
			where f.encId=#encId# and f.formType=#formType# and f.formModel=#formModel#
		) t
		where rowN = 1 ${WITHUR}

SELECT formID, formType, formModel, encId, patientId, stationId, totalScore, EvaluationTime, CreateTime, createUserId, createUserName, RECORD_POID, CONTENT, formItems, formVersionId, STATES, BEDID, receiveTime, receiveUserId, receiveUserName, CALM_ID, EVENTIDS FROM ${FORM} where encId=#encId# and formType IN
#formTypes[]#
order by EvaluationTime ASC ${WITHUR}

SELECT formID, formType, formModel, encId, patientId, stationId, totalScore, EvaluationTime, CreateTime, createUserId, createUserName, RECORD_POID, CONTENT, formItems, formVersionId, STATES, BEDID, receiveTime, receiveUserId, receiveUserName, CALM_ID, EVENTIDS FROM ${FORM} where encId=#encId# and formType IN
#formTypes[]#
order by EvaluationTime desc ${WITHUR}

SELECT FORM.*, CHECKNURSINGRECORD.ID CHECKID, CHECKNURSINGRECORD.MODIFYDATE CHECKMODIFYDATE, CHECKNURSINGRECORD.USERNAME CHECKUSERNAME FROM ${FORM} left join ${CHECKNURSINGRECORD} on ${FORM}.FORMID=${CHECKNURSINGRECORD}.FORMID where FORM.encId=#encId# and
${FORM}.formType=#formType# and ${FORM}.formModel=#formModel#
and ${FORM}.CreateTime>=#beginDate# and ${FORM}.CreateTime<=#endDate# ${WITHUR}

select f.FORMID,f.FORMTYPE,f.FORMMODEL,f.TOTALSCORE,f.CREATETIME,f.CREATEUSERID,f.CREATEUSERNAME,f.RECORD_POID,f.RECEIVETIME,f.RECEIVEUSERID,f.RECEIVEUSERNAME,f.EVALUATIONTIME,f.EVENTIDS, c.ID CHECKID, c.MODIFYDATE CHECKMODIFYDATE, c.USERNAME CHECKUSERNAME from ${FORM} f left join ${CHECKNURSINGRECORD} c on f.FORMID=c.FORMID where
f.ENCID=#encId# and f.PATIENTID=#patientId# and f.EVALUATIONTIME>=#beginDate# and f.EVALUATIONTIME<=#endDate# and f.FORMTYPE=#formType# and f.FORMMODEL=#formModel#
order by f.EVALUATIONTIME asc ${WITHUR}
